def solution(s: str) -> int:
    n = len(s)
    if n == 0:
        return 0

    # dp[i][j] represents the minimum number of palindromic subsequences
    # needed to partition s[i:j+1]
    dp = [[0] * n for _ in range(n)]

    # Base case: single characters are palindromes
    for i in range(n):
        dp[i][i] = 1

    # Fill the dp table for all substring lengths
    for length in range(2, n + 1):  # length of substring
        for i in range(n - length + 1):
            j = i + length - 1

            if s[i] == s[j]:
                if length == 2:
                    dp[i][j] = 1  # Two same characters form one palindrome
                else:
                    dp[i][j] = dp[i+1][j-1]  # Same as inner substring
            else:
                # Try all possible splits
                dp[i][j] = float('inf')
                for k in range(i, j):
                    dp[i][j] = min(dp[i][j], dp[i][k] + dp[k+1][j])

    return dp[0][n-1]

