class SemanticIndex:
    def __init__(self):
        self.docs = {}
        self.vocab = set()
        self.idf_cache = {}

    def add_document(self, doc_id, text):
        words = self._tokenize(text)
        self.docs[doc_id] = words
        self.vocab.update(words)
        self.idf_cache.clear()

    def _tokenize(self, text):
        return re.findall(r'\b\w+\b', text.lower())

    def _compute_idf(self):
        if self.idf_cache:
            return self.idf_cache

        doc_freq = defaultdict(int)
        for words in self.docs.values():
            for word in set(words):
                doc_freq[word] += 1

        total_docs = len(self.docs)
        for word in self.vocab:
            self.idf_cache[word] = math.log(total_docs / max(doc_freq[word], 1))
        return self.idf_cache

    def _tfidf_vector(self, words):
        tf = Counter(words)
        idf = self._compute_idf()
        vector = []
        for word in sorted(self.vocab):
            tf_score = tf.get(word, 0) / max(len(words), 1)
            tfidf_score = tf_score * idf.get(word, 0)
            vector.append(tfidf_score)
        return vector

    def search(self, query, top_k=None):
        query_words = self._tokenize(query)
        query_vec = self._tfidf_vector(query_words)

        results = []
        for doc_id, doc_words in self.docs.items():
            doc_vec = self._tfidf_vector(doc_words)
            similarity = self._cosine_similarity(query_vec, doc_vec)
            results.append((doc_id, similarity))

        results.sort(key=lambda x: -x[1])
        return results[:top_k] if top_k else results

    def _cosine_similarity(self, a, b):
        dot = sum(x * y for x, y in zip(a, b))
        norm_a = math.sqrt(sum(x * x for x in a))
        norm_b = math.sqrt(sum(x * x for x in b))
        return dot / (norm_a * norm_b) if norm_a and norm_b else 0.0

