class EMATracker:
    def __init__(self, alpha=0.3):
        self.alpha = alpha
        self.values = {}
    def update(self, key, value):
        if key not in self.values:
            self.values[key] = value
        else:
            self.values[key] = self.alpha * value + (1 - self.alpha) * self.values[key]
        return round(self.values[key], 4)

